package com.tns.firstprog;
import java.util.Scanner;
public class Temperature {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp = 30 ;
		for(int i=1;i<=5;i++)
		{
			temp=temp-2;
			System.out.println("Week:" +i);
			System.out.println("Temp:" +temp);
			
		}
	}

}